.calculation-box {
  display: inline-flex;
  flex-flow: column;
  height: 20vh;
  width: 31vh;
  position: relative;
  background-color: rgba(255, 255, 255, 1.0);
  border-width: 0px;
  color: #696969;
  text-align: left;
  border-radius: 5px;
  z-index: 1;
  box-shadow: 0px 5px 15px 0px rgba(128, 128, 128, 0.2);
  margin-right: 1vh;
  margin-top: 1vh;
  margin-bottom: 1vh;
}

.graph-box {
  display: inline-flex;
  flex-flow: column;
  height: 35vh;
  width: 47.5vh;
  position: relative;
  background-color: rgba(255, 255, 255, 1.0);
  border-width: 0px;
  color: #696969;
  text-align: left;
  border-radius: 5px;
  z-index: 1;
  box-shadow: 0px 5px 15px 0px rgba(128, 128, 128, 0.2);
  margin-right: 1vh;
  margin-top: 1vh;
}

@media screen and (max-width: 960px) {
  .calculation-box {
    width: 45vw;
    /* The width is 100%, when the viewport is 800px or smaller */
  }
